function UserListExample() {
  const users = [
    {
      id: 1,
      name: "John Doe",
      email: "john@example.com",
      status: "online",
      role: "Admin"
    },
    {
      id: 2,
      name: "Jane Smith",
      email: "jane@example.com",
      status: "offline",
      role: "Editor"
    },
    {
      id: 3,
      name: "Bob Johnson",
      email: "bob@example.com",
      status: "away",
      role: "User"
    },
  ];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online': return 'bg-green-500';
      case 'away': return 'bg-yellow-500';
      case 'offline': return 'bg-gray-500';
      default: return 'bg-gray-500';
    }
  };

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase();
  };

  return (
    <div className="space-y-4">
      {users.map((user) => (
        <div key={user.id} className="flex items-center space-x-4 p-4 border rounded-lg">
          <div className="relative">
            <Avatar>
              <AvatarFallback>{getInitials(user.name)}</AvatarFallback>
            </Avatar>
            <div className={`absolute -bottom-1 -right-1 h-3 w-3 rounded-full border-2 border-white ${getStatusColor(user.status)}`} />
          </div>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-gray-900 truncate">
              {user.name}
            </p>
            <p className="text-sm text-gray-500 truncate">
              {user.email}
            </p>
          </div>
          <Badge variant={user.role === 'Admin' ? 'default' : 'secondary'}>
            {user.role}
          </Badge>
        </div>
      ))}
    </div>
  );
}

